story_response_format.structure:
{
  "story": "your story text here with **bolded** vocabulary words...",
  "entities": {
    "characters": {
      "named": ["list only actual character names like 'Leo', 'Sarah', 'Captain Smith'"],
      "unnamed": ["list only SEPARATE unnamed characters like 'the wizard', 'a friendly robot' - DO NOT include descriptors of already named characters"]
    },
    "locations": {
      "named": ["list only actual location names like 'Planet Zorba', 'Crystal Cave'"],
      "unnamed": ["list only SEPARATE unnamed locations like 'a mysterious forest', 'an ancient castle' - DO NOT include descriptors of already named locations"]
    }
  },
  "vocabulary_words": ["list the bolded vocabulary words here"]
}

vocabulary_question_format.structure:
{
  "question": "What does [word] mean?",
  "options": {
    "a": "first option",
    "b": "second option", 
    "c": "third option",
    "d": "fourth option"
  },
  "correct_answer": "letter of correct option",
  "explanation": "brief explanation of the correct answer"
}

feedback_format.structure:
{
  "feedback": "your encouraging feedback here",
  "suggestions": ["specific improvement suggestions if any"],
  "praise": "positive reinforcement message"
}

consolidated_response_format.structure:
{
  "story_content": "generated story content",
  "vocabulary_question": {
    "question": "vocabulary question",
    "options": {"a": "option1", "b": "option2", "c": "option3", "d": "option4"},
    "correct_answer": "a",
    "explanation": "explanation text"
  },
  "writing_feedback": {
    "feedback": "feedback text",
    "suggestions": ["suggestions array"],
    "praise": "praise text"
  },
  "next_step": "guidance for continuation"
}
